import React, { Component } from 'react'
import "./App.css"

class App extends Component {

  constructor(props) {
    super(props)

    this.state = {
       job: "",
       alljobs : [""],
       write : ""
    }

  }
  handleJobChange = (event) =>{
    this.setState({
      job : event.target.value
    })
  }
  handleJobSubmit = (event) => {
    this.setState(prevState => ({
      alljobs: [...prevState.alljobs, this.state.job],
    }))

  }
  render() {

    return (
      <div className='App'>
        <h1>My list</h1>
        <button>Clear list</button>
        <ul>
          {this.state.alljobs.map((value,index) => <li>{value}</li>)}
        </ul>
        <form>
          <input type="text" onChange={this.handleJobChange}/> <br></br>
          <input type="submit" value="Add item" onClick={this.handleJobSubmit}/>
        </form>
      </div>
    )
  }
}

export default App;
